import "./IPeripheryImmutableState.sol";
import "./IERC721Permit.sol";

interface INonfungiblePositionManager is
    IMintCallback,
    IPeripheryPayments,
    IPeripheryImmutableState,
    IERC721Metadata,
    IERC721Enumerable,
    IERC721Permit
{
    event IncreaseLiquidity(
        uint256 indexed tokenId,
        address to,
        address pool,
        bytes32 positionKey,
        address token,
        uint256 liquidity
    );
    event DecreaseLiquidity(
        uint256 indexed tokenId,
        address to,
        address pool,
        bytes32 positionKey,
        address token,
        uint256 liquidity
    );
    event Collect(
        uint256 indexed tokenId,
        address to,
        address pool,
        bytes32 positionKey,
        address token,
        uint256 amount
    );

    function positions(
        uint256 tokenId
    )
        external
        view
        returns (
            uint96 nonce,
            address operator,
            address token,
            uint256 liquidity,
            uint256 claimableFee
        );

    struct MintParams {
        address token;
        uint256 liquidity;
        address to;
        uint256 deadline;
    }

    function mint(
        MintParams calldata params
    ) external payable returns (uint256 tokenId, bytes32 positionKey);

    struct IncreaseLiquidityParams {
        uint256 tokenId;
        uint256 liquidity;
        uint256 deadline;
    }

    function increaseLiquidity(
        IncreaseLiquidityParams calldata params
    ) external payable returns (bytes32 positionKey);

    struct DecreaseLiquidityParams {
        uint256 tokenId;
        uint256 liquidity;
        address to;
        uint256 deadline;
    }

    function decreaseLiquidity(
        DecreaseLiquidityParams calldata params
    ) external payable returns (uint256 amount);

    struct CollectParams {
        uint256[] tokenIds;
        address to;
    }

    function collect(
        CollectParams calldata params
    ) external payable returns (uint256 amount);

    function burn(uint256 tokenId) external payable;
}
